/**
 * Enhanced Settings Panel with Job Queue Integration
 *
 * This component extends the original SettingsPanel to support background job processing
 * with real-time progress tracking and enhanced user experience.
 *
 * Key Enhancements:
 * - Asynchronous job submission with immediate feedback
 * - Priority selection for processing jobs
 * - Memory optimization toggle
 * - Real-time job progress integration
 * - Bulk processing capabilities
 * - Advanced processing options
 *
 * @format
 */

import React, { useState, useEffect } from "react";
import { useToast } from "@/hooks/use-toast";
import { ProcessingSettings } from "@shared/schema";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
	Select,
	SelectContent,
	SelectItem,
	SelectTrigger,
	SelectValue,
} from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Label } from "@/components/ui/label";
import { Separator } from "@/components/ui/separator";
import {
	Zap,
	Settings,
	Clock,
	MemoryStick,
	Layers,
	AlertTriangle,
} from "lucide-react";

// Job priority options
export enum JobPriority {
	LOW = 1,
	NORMAL = 2,
	HIGH = 3,
	CRITICAL = 4,
}

interface EnhancedSettingsPanelProps {
	/** ID of the track to process, null when no track available */
	trackId: number | null;
	/** Callback fired when processing begins successfully */
	onProcessingStart: (jobId: string) => void;
	/** Whether the panel should be disabled (e.g., during processing) */
	disabled?: boolean;
	/** Whether to show advanced options */
	showAdvanced?: boolean;
	/** Current job ID if processing is active */
	activeJobId?: string | null;
}

export const EnhancedSettingsPanel: React.FC<EnhancedSettingsPanelProps> = ({
	trackId,
	onProcessingStart,
	disabled = false,
	showAdvanced = true,
	activeJobId = null,
}) => {
	const { toast } = useToast();

	// Core processing settings
	const [settings, setSettings] = useState<ProcessingSettings>({
		introLength: 16,
		outroLength: 16,
		preserveVocals: true,
		beatDetection: "auto",
	});

	// Enhanced options
	const [priority, setPriority] = useState<JobPriority>(JobPriority.NORMAL);
	const [useOptimization, setUseOptimization] = useState(true);
	const [estimatedTime, setEstimatedTime] = useState<string>("2-5 minutes");
	const [isSubmitting, setIsSubmitting] = useState(false);

	/**
	 * Calculate estimated processing time based on settings
	 */
	useEffect(() => {
		const baseTime = 3; // Base processing time in minutes

		// Factors that affect processing time
		const complexityFactor = settings.preserveVocals ? 1.3 : 1.0;
		const beatDetectionFactor = settings.beatDetection === "madmom" ? 1.2 : 1.0;
		const lengthFactor = (settings.introLength + settings.outroLength) / 32;
		const optimizationFactor = useOptimization ? 0.7 : 1.0;
		const priorityFactor =
			priority === JobPriority.HIGH
				? 0.8
				: priority === JobPriority.LOW
				? 1.4
				: 1.0;

		const estimatedMinutes = Math.ceil(
			baseTime *
				complexityFactor *
				beatDetectionFactor *
				lengthFactor *
				optimizationFactor *
				priorityFactor
		);

		setEstimatedTime(
			`${Math.max(1, estimatedMinutes - 1)}-${estimatedMinutes + 2} minutes`
		);
	}, [settings, useOptimization, priority]);

	/**
	 * Handle intro length adjustment
	 */
	const incrementIntroLength = () => {
		if (settings.introLength < 64) {
			setSettings((prev) => ({
				...prev,
				introLength: prev.introLength + 8,
			}));
		}
	};

	const decrementIntroLength = () => {
		if (settings.introLength > 8) {
			setSettings((prev) => ({
				...prev,
				introLength: prev.introLength - 8,
			}));
		}
	};

	/**
	 * Handle outro length adjustment
	 */
	const incrementOutroLength = () => {
		if (settings.outroLength < 64) {
			setSettings((prev) => ({
				...prev,
				outroLength: prev.outroLength + 8,
			}));
		}
	};

	const decrementOutroLength = () => {
		if (settings.outroLength > 8) {
			setSettings((prev) => ({
				...prev,
				outroLength: prev.outroLength - 8,
			}));
		}
	};

	/**
	 * Toggle vocal preservation
	 */
	const togglePreserveVocals = () => {
		setSettings((prev) => ({
			...prev,
			preserveVocals: !prev.preserveVocals,
		}));
	};

	/**
	 * Handle beat detection algorithm change
	 */
	const handleBeatDetectionChange = (value: string) => {
		setSettings((prev) => ({
			...prev,
			beatDetection: value as "auto" | "librosa" | "madmom",
		}));
	};

	/**
	 * Handle priority change
	 */
	const handlePriorityChange = (value: string) => {
		setPriority(parseInt(value, 10) as JobPriority);
	};

	/**
	 * Submit processing job to queue
	 */
	const handleGenerateClick = async () => {
		if (!trackId) {
			toast({
				title: "No track selected",
				description: "Please upload a track first.",
				variant: "destructive",
			});
			return;
		}

		setIsSubmitting(true);

		try {
			const response = await fetch(`/api/tracks/${trackId}/process-async`, {
				method: "POST",
				headers: {
					"Content-Type": "application/json",
				},
				body: JSON.stringify({
					...settings,
					priority,
					useOptimization,
				}),
			});

			if (!response.ok) {
				const errorData = await response.json();
				throw new Error(
					errorData.message || `Failed to start processing: ${response.status}`
				);
			}

			const result = await response.json();

			toast({
				title: "Processing Queued",
				description: `Your track is now queued for processing. Job ID: ${result.jobId.substring(
					0,
					8
				)}...`,
				duration: 5000,
			});

			// Notify parent component with job ID
			onProcessingStart(result.jobId);
		} catch (error) {
			console.error("Processing error:", error);
			toast({
				title: "Processing Failed",
				description:
					(error as Error).message || "An unexpected error occurred.",
				variant: "destructive",
			});
		} finally {
			setIsSubmitting(false);
		}
	};

	/**
	 * Cancel active job
	 */
	const handleCancelJob = async () => {
		if (!activeJobId) return;

		try {
			const response = await fetch(`/api/jobs/${activeJobId}`, {
				method: "DELETE",
			});

			if (!response.ok) {
				throw new Error("Failed to cancel job");
			}

			toast({
				title: "Job Cancelled",
				description: "Processing job has been cancelled.",
				duration: 3000,
			});
		} catch (error) {
			console.error("Cancel job error:", error);
			toast({
				title: "Cancel Failed",
				description: "Failed to cancel the processing job.",
				variant: "destructive",
			});
		}
	};

	/**
	 * Get priority badge variant and text
	 */
	const getPriorityDisplay = (priorityValue: JobPriority) => {
		const priorities = {
			[JobPriority.LOW]: {
				text: "Low",
				variant: "secondary" as const,
				icon: Clock,
			},
			[JobPriority.NORMAL]: {
				text: "Normal",
				variant: "outline" as const,
				icon: Settings,
			},
			[JobPriority.HIGH]: {
				text: "High",
				variant: "default" as const,
				icon: Zap,
			},
			[JobPriority.CRITICAL]: {
				text: "Critical",
				variant: "destructive" as const,
				icon: AlertTriangle,
			},
		};
		return priorities[priorityValue];
	};

	const currentPriority = getPriorityDisplay(priority);
	const PriorityIcon = currentPriority.icon;

	return (
		// skipcq: JS-0415
		<Card className='w-full'>
			<CardHeader>
				<CardTitle className='flex items-center justify-between'>
					<span>Audio Processing Settings</span>
					{activeJobId && (
						<Badge variant='default' className='flex items-center gap-1'>
							<Zap size={12} />
							Processing Active
						</Badge>
					)}
				</CardTitle>
			</CardHeader>

			<CardContent className='space-y-6'>
				{/* Core Settings */}
				<div className='space-y-4'>
					<h3 className='text-lg font-semibold'>Extension Parameters</h3>

					{/* Intro Length */}
					<div className='flex items-center justify-between'>
						<Label className='font-medium'>
							Intro Length: {settings.introLength} bars
						</Label>
						<div className='flex items-center gap-2'>
							<Button
								variant='outline'
								size='sm'
								// skipcq: JS-0417
								onClick={decrementIntroLength}
								disabled={disabled || settings.introLength <= 8}>
								-
							</Button>
							<Badge variant='secondary'>{settings.introLength} bars</Badge>
							<Button
								variant='outline'
								size='sm'
								// skipcq: JS-0417
								onClick={incrementIntroLength}
								disabled={disabled || settings.introLength >= 64}>
								+
							</Button>
						</div>
					</div>

					{/* Outro Length */}
					<div className='flex items-center justify-between'>
						<Label className='font-medium'>
							Outro Length: {settings.outroLength} bars
						</Label>
						<div className='flex items-center gap-2'>
							<Button
								variant='outline'
								size='sm'
								// skipcq: JS-0417
								onClick={decrementOutroLength}
								disabled={disabled || settings.outroLength <= 8}>
								-
							</Button>
							<Badge variant='secondary'>{settings.outroLength} bars</Badge>
							<Button
								variant='outline'
								size='sm'
								// skipcq: JS-0417
								onClick={incrementOutroLength}
								disabled={disabled || settings.outroLength >= 64}>
								+
							</Button>
						</div>
					</div>

					{/* Preserve Vocals */}
					<div className='flex items-center justify-between'>
						<Label className='font-medium'>Preserve Vocals</Label>
						<Switch
							checked={settings.preserveVocals}
							// skipcq: JS-0417
							onCheckedChange={togglePreserveVocals}
							disabled={disabled}
						/>
					</div>

					{/* Beat Detection */}
					<div className='flex items-center justify-between'>
						<Label className='font-medium'>Beat Detection Algorithm</Label>
						<Select
							value={settings.beatDetection}
							// skipcq: JS-0417
							onValueChange={handleBeatDetectionChange}
							disabled={disabled}>
							<SelectTrigger className='w-32'>
								<SelectValue />
							</SelectTrigger>
							<SelectContent>
								<SelectItem value='auto'>Auto</SelectItem>
								<SelectItem value='librosa'>Librosa</SelectItem>
								<SelectItem value='madmom'>Madmom</SelectItem>
							</SelectContent>
						</Select>
					</div>
				</div>

				{/* Advanced Settings */}
				{showAdvanced && (
					// skipcq: JS-0415
					<>
						<Separator />
						{/* skipcq: JS-0415 */}
						<div className='space-y-4'>
							<h3 className='text-lg font-semibold flex items-center gap-2'>
								<Settings size={18} />
								Advanced Options
							</h3>

							{/* Processing Priority */}
							<div className='flex items-center justify-between'>
								<Label className='font-medium'>Processing Priority</Label>
								<div className='flex items-center gap-2'>
									<Select
										value={priority.toString()}
										// skipcq: JS-0417
										onValueChange={handlePriorityChange}
										disabled={disabled}>
										<SelectTrigger className='w-32'>
											<SelectValue />
										</SelectTrigger>
										<SelectContent>
											<SelectItem value={JobPriority.LOW.toString()}>
												Low
											</SelectItem>
											<SelectItem value={JobPriority.NORMAL.toString()}>
												Normal
											</SelectItem>
											<SelectItem value={JobPriority.HIGH.toString()}>
												High
											</SelectItem>
											<SelectItem value={JobPriority.CRITICAL.toString()}>
												Critical
											</SelectItem>
										</SelectContent>
									</Select>
									<Badge
										variant={currentPriority.variant}
										className='flex items-center gap-1'>
										<PriorityIcon size={12} />
										{currentPriority.text}
									</Badge>
								</div>
							</div>

							{/* Memory Optimization */}
							<div className='flex items-center justify-between'>
								<div>
									<Label className='font-medium flex items-center gap-2'>
										<MemoryStick size={16} />
										Memory Optimization
									</Label>
									<p className='text-sm text-gray-500 mt-1'>
										Use optimized processing for reduced memory usage
									</p>
								</div>
								<Switch
									checked={useOptimization}
									onCheckedChange={setUseOptimization}
									disabled={disabled}
								/>
							</div>

							{/* Processing Info */}
							<div className='p-4 bg-blue-50 rounded-lg border border-blue-200'>
								<div className='flex items-center gap-2 mb-2'>
									<Clock size={16} className='text-blue-600' />
									<span className='font-medium text-blue-800'>
										Estimated Processing Time
									</span>
								</div>
								<p className='text-blue-700'>{estimatedTime}</p>

								{useOptimization && (
									<div className='flex items-center gap-2 mt-2'>
										<MemoryStick size={14} className='text-green-600' />
										<span className='text-sm text-green-700'>
											Memory optimized (60-80% reduction)
										</span>
									</div>
								)}
							</div>
						</div>
					</>
				)}

				{/* Action Buttons */}
				<Separator />
				<div className='flex flex-col gap-3'>
					{!activeJobId ? (
						<Button
							// skipcq: JS-0417
							onClick={handleGenerateClick}
							disabled={disabled || !trackId || isSubmitting}
							className='w-full flex items-center justify-center gap-2'
							size='lg'>
							{isSubmitting ? (
								<>
									<div className='animate-spin rounded-full h-4 w-4 border-b-2 border-white' />
									Queueing...
								</>
							) : (
								<>
									<Layers size={16} />
									Queue Processing Job
								</>
							)}
						</Button>
					) : (
						<Button
							// skipcq: JS-0417
							onClick={handleCancelJob}
							variant='destructive'
							className='w-full flex items-center justify-center gap-2'
							size='lg'>
							<AlertTriangle size={16} />
							Cancel Processing
						</Button>
					)}

					{/* Queue Benefits */}
					<div className='p-3 bg-green-50 rounded-lg border border-green-200'>
						<h4 className='font-medium text-green-800 mb-2'>
							Background Processing Benefits:
						</h4>
						<ul className='text-sm text-green-700 space-y-1'>
							<li>• Non-blocking: Continue using the app while processing</li>
							<li>• Real-time progress updates via WebSocket</li>
							<li>• Automatic retry on temporary failures</li>
							<li>• Priority-based processing queue</li>
							{useOptimization && <li>• Memory-optimized for large files</li>}
						</ul>
					</div>
				</div>
			</CardContent>
		</Card>
	);
};

export default EnhancedSettingsPanel;

/** @format */

import React, { useState } from "react";
import { useToast } from "@/hooks/use-toast";
import { ProcessingSettings } from "@shared/schema";

/**
 * SettingsPanel Component
 *
 * A comprehensive audio processing configuration interface that allows users to customize
 * how their tracks are extended and processed. This component features:
 *
 * Core Features:
 * - Interactive controls for intro/outro length adjustment (8-64 bar range)
 * - Synchronized intro/outro length management for consistent workflow
 * - Audio processing options (vocal preservation, beat detection methods)
 * - Real-time validation and user feedback through toast notifications
 * - Disabled state management during processing to prevent conflicts
 * - Intelligent error handling with descriptive user messages
 *
 * State Management:
 * - Processing settings with default values optimized for most tracks
 * - Submission state tracking to prevent duplicate requests
 * - Form validation ensuring settings are within acceptable ranges
 *
 * The component integrates seamlessly with the audio processing pipeline,
 * sending validated settings to the server and managing the processing lifecycle.
 */
interface SettingsPanelProps {
	/** ID of the track to process, null when no track available */
	trackId: number | null;
	/** Callback fired when processing begins successfully */
	onProcessingStart: () => void;
	/** Whether the panel should be disabled (e.g., during processing) */
	disabled?: boolean;
}

const SettingsPanel: React.FC<SettingsPanelProps> = ({
	trackId,
	onProcessingStart,
	disabled = false,
}) => {
	// Processing settings state with optimized defaults for most tracks
	const [settings, setSettings] = useState<ProcessingSettings>({
		introLength: 16, // 16-bar intro (typical for DJ mixing)
		outroLength: 16, // 16-bar outro (matches intro for seamless loops)
		preserveVocals: true, // Keep vocals intact by default
		beatDetection: "auto", // Automatic beat detection algorithm selection
	});

	// Submission state to prevent duplicate processing requests
	const [isSubmitting, setIsSubmitting] = useState(false);
	const { toast } = useToast();

	/**
	 * Increment intro length in 8-bar increments (DJ-friendly)
	 * Also updates outro length to maintain consistency for DJ mixing
	 * Maximum limit of 64 bars to prevent excessively long intros
	 */
	const incrementIntroLength = () => {
		if (settings.introLength < 64) {
			setSettings((prev) => ({
				...prev,
				introLength: prev.introLength + 8,
				outroLength: prev.outroLength + 8, // Keep intro/outro synchronized
			}));
		}
	};

	/**
	 * Decrement intro length in 8-bar increments
	 * Maintains synchronization with outro length
	 * Minimum limit of 8 bars to ensure adequate mixing time
	 */
	const decrementIntroLength = () => {
		if (settings.introLength > 8) {
			setSettings((prev) => ({
				...prev,
				introLength: prev.introLength - 8,
				outroLength: prev.outroLength - 8, // Keep intro/outro synchronized
			}));
		}
	};

	/**
	 * Main processing initiation function with comprehensive error handling
	 *
	 * This function:
	 * 1. Validates track selection
	 * 2. Sends processing settings to server
	 * 3. Handles various error scenarios
	 * 4. Provides user feedback through toast notifications
	 * 5. Manages submission state to prevent duplicate requests
	 */
	const handleGenerateClick = async () => {
		// Validate track selection before proceeding
		if (!trackId) {
			toast({
				title: "No track selected",
				description: "Please upload a track first.",
				variant: "destructive",
			});
			return;
		}

		// Set submitting state to disable form and show loading feedback
		setIsSubmitting(true);

		try {
			// Send processing request with current settings to server
			const response = await fetch(`/api/tracks/${trackId}/process`, {
				method: "POST",
				headers: {
					"Content-Type": "application/json",
				},
				body: JSON.stringify(settings),
			});

			if (!response.ok) {
				throw new Error(
					`Failed to start processing: ${response.status} ${response.statusText}`
				);
			}

			// Show success notification to user
			toast({
				title: "Processing Started",
				description:
					"Your track is now being processed. This may take a few minutes.",
			});

			// Notify parent component that processing has begun
			onProcessingStart();
		} catch (error) {
			// skipcq: JS-0002
			console.error("Processing error:", error);

			// Show error notification with descriptive message
			toast({
				title: "Processing Failed",
				description:
					(error as Error).message || "An unexpected error occurred.",
				variant: "destructive",
			});
		} finally {
			// Reset submission state regardless of outcome
			setIsSubmitting(false);
		}
	};

	return (
		// skipcq: JS-0415
		<div className='bg-white rounded-xl shadow-md p-6'>
			<h2 className='text-xl font-semibold mb-4'>Extension Settings</h2>

			<div className='space-y-4'>
				<div>
					{/* skipcq: JS-0752 */}
					<h5 className='block text-sm font-medium text-gray-700 mb-1'>
						Intro Length (bars)
					</h5>
					<div className='flex items-center'>
						<button
							className='bg-gray-200 px-2 py-1 rounded-l-md disabled:opacity-50'
							// skipcq: JS-0417
							onClick={decrementIntroLength}
							disabled={settings.introLength <= 8 || disabled}>
							<span className='material-icons text-sm'>remove</span>
						</button>
						<div className='px-4 py-1 bg-gray-100 text-center'>
							{settings.introLength}
						</div>
						<button
							className='bg-gray-200 px-2 py-1 rounded-r-md disabled:opacity-50'
							// skipcq: JS-0417
							onClick={incrementIntroLength}
							disabled={settings.introLength >= 64 || disabled}>
							<span className='material-icons text-sm'>add</span>
						</button>
					</div>
				</div>
			</div>

			<button
				className='mt-6 w-full bg-gradient-to-r from-primary to-purple-600 text-white py-2 px-4 rounded-md font-medium shadow hover:shadow-md focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary disabled:opacity-50'
				// skipcq: JS-0417
				onClick={handleGenerateClick}
				disabled={!trackId || isSubmitting || disabled}
				data-generate-button='true'>
				{isSubmitting ? "Starting Process..." : "Generate Extended Version"}
			</button>
		</div>
	);
};

export default SettingsPanel;

/** @format */

import React, { useState, useRef, useEffect } from "react";
import { AudioTrack } from "@shared/schema";
import { formatDuration } from "@/lib/audio";
import { useToast } from "@/hooks/use-toast";
import { queryClient } from "@/lib/queryClient";
import VersionPlayer from "./VersionPlayer";

interface TrackViewProps {
	track: AudioTrack;
	type: "original" | "extended";
	version: number;
}

// skipcq: JS-R1005
const TrackView: React.FC<TrackViewProps> = ({ track, type, version }) => {
	const [isPlaying, setIsPlaying] = useState(false);
	const [currentTime, setCurrentTime] = useState(0);
	const [duration, setDuration] = useState(0);
	const [isProcessing, setIsProcessing] = useState(() => {
		return (
			localStorage.getItem(`processing_${track.id}`) === "processing" ||
			localStorage.getItem(`processing_${track.id}`) === "regenerate"
		);
	});

	const audioRef = useRef<HTMLAudioElement>(null);
	const progressIntervalRef = useRef<number>();
	const statusIntervalRef = useRef<number>(); // Added ref for status polling interval
	const { toast } = useToast();

	const displayTitle =
		type === "original"
			? track.originalFilename
			: `${track.originalFilename.replace(/\.[^/.]+$/, "")} (Extended Mix v${
					version + 1
			  })${track.originalFilename.match(/\.[^/.]+$/)?.[0] || ""}`;

	const displayDuration =
		type === "original"
			? track.duration || 0
			: track.extendedDurations?.[version] || 0;

	const displayDetails = `${
		type === "original" ? "Original" : "Extended"
	} • ${formatDuration(displayDuration)} ${
		track.bpm ? `• ${track.bpm} BPM` : ""
	}`;

	// Initial status check effect
	// skipcq: JS-0045
	useEffect(() => {
		if (isProcessing) {
			const checkInitialStatus = async () => {
				try {
					const response = await fetch(`/api/tracks/${track.id}/status`);
					const data = await response.json();
					if (data.status === "completed") {
						setIsProcessing(false);
						localStorage.removeItem(`processing_${track.id}`);
						queryClient.invalidateQueries({
							queryKey: [`/api/tracks/${track.id}`],
						});
					}
				} catch (error) {
					console.error("Error checking status:", error);
				}
			};
			checkInitialStatus();
		}
	}, [track.id]);

	// Status polling effect
	// skipcq: JS-0045
	useEffect(() => {
		if (isProcessing) {
			const pollStatus = async () => {
				try {
					const response = await fetch(`/api/tracks/${track.id}/status`, {
						headers: {
							"Cache-Control": "no-cache",
							Pragma: "no-cache",
						},
					});

					if (!response.ok) throw new Error("Status check failed");
					const data = await response.json();

					if (data.status === "processing" || data.status === "regenerate") {
						localStorage.setItem(`processing_${track.id}`, data.status);
					} else if (data.status === "completed") {
						setIsProcessing(false);
						await queryClient.invalidateQueries({
							queryKey: [`/api/tracks/${track.id}`],
						});
						localStorage.removeItem(`processing_${track.id}`);

						// Clear the interval
						if (statusIntervalRef.current) {
							clearInterval(statusIntervalRef.current);
							statusIntervalRef.current = undefined;
						}

						toast({
							title: "Success",
							description: "Track processing completed!",
							duration: 3000,
						});
					} else if (data.status === "error") {
						setIsProcessing(false);
						localStorage.removeItem(`processing_${track.id}`);

						// Clear the interval
						if (statusIntervalRef.current) {
							clearInterval(statusIntervalRef.current);
							statusIntervalRef.current = undefined;
						}

						toast({
							title: "Error",
							description: "Processing failed",
							variant: "destructive",
							duration: 5000,
						});
					}
				} catch (error) {
					console.error("Error in status check:", error);
				}
			};

			// Initial poll
			pollStatus();

			// Set up polling interval
			statusIntervalRef.current = window.setInterval(pollStatus, 2000);

			// Return cleanup function
			// skipcq: JS-0045
			return () => {
				if (statusIntervalRef.current) {
					clearInterval(statusIntervalRef.current);
					statusIntervalRef.current = undefined;
				}
			};
		}
	}, [isProcessing, track.id, toast]);

	// skipcq: JS-0045
	useEffect(() => {
		if (audioRef.current) {
			const audio = audioRef.current;

			const onLoadedMetadata = () => {
				setDuration(audio.duration);
			};

			const onEnded = () => {
				setIsPlaying(false);
				setCurrentTime(0);
				clearInterval(progressIntervalRef.current);
			};

			audio.addEventListener("loadedmetadata", onLoadedMetadata);
			audio.addEventListener("ended", onEnded);

			// skipcq: JS-0045
			return () => {
				audio.removeEventListener("loadedmetadata", onLoadedMetadata);
				audio.removeEventListener("ended", onEnded);
				clearInterval(progressIntervalRef.current);
			};
		}
	}, [track.id, type]);

	const togglePlayPause = () => {
		if (!audioRef.current) return;

		if (isPlaying) {
			audioRef.current.pause();
			clearInterval(progressIntervalRef.current);
			setIsPlaying(false);
		} else {
			audioRef.current.play();
			progressIntervalRef.current = window.setInterval(() => {
				if (audioRef.current) {
					setCurrentTime(audioRef.current.currentTime);
				}
			}, 100);
			setIsPlaying(true);
		}
	};

	const handleSkipBack = () => {
		if (!audioRef.current) return;
		audioRef.current.currentTime = Math.max(
			0,
			audioRef.current.currentTime - 10
		);
		setCurrentTime(audioRef.current.currentTime);
	};

	const handleSkipForward = () => {
		if (!audioRef.current) return;
		audioRef.current.currentTime = Math.min(
			audioRef.current.duration,
			audioRef.current.currentTime + 10
		);
		setCurrentTime(audioRef.current.currentTime);
	};

	const handleProgressClick = (e: React.MouseEvent<HTMLDivElement>) => {
		if (!audioRef.current || !duration) return;

		const progressBar = e.currentTarget;
		const rect = progressBar.getBoundingClientRect();
		const pos = (e.clientX - rect.left) / rect.width;
		const newTime = pos * duration;

		audioRef.current.currentTime = newTime;
		setCurrentTime(newTime);
	};

	/**
	 * Handles keyboard events for progress bar accessibility
	 * Left/Right arrows and Home/End keys for seeking
	 */
	const handleProgressKeyDown = (e: React.KeyboardEvent<HTMLDivElement>) => {
		if (!audioRef.current || !duration) return;

		let newTime = currentTime;
		const seekStep = duration * 0.05; // 5% of total duration

		switch (e.key) {
			case "ArrowLeft":
				newTime = Math.max(0, currentTime - seekStep);
				break;
			case "ArrowRight":
				newTime = Math.min(duration, currentTime + seekStep);
				break;
			case "Home":
				newTime = 0;
				break;
			case "End":
				newTime = duration;
				break;
			default:
				return; // Don't prevent default for other keys
		}

		e.preventDefault();
		audioRef.current.currentTime = newTime;
		setCurrentTime(newTime);
	};

	const handleRegenerate = async () => {
		try {
			setIsProcessing(true);
			const processResponse = await fetch(`/api/tracks/${track.id}/process`, {
				method: "POST",
				headers: {
					"Content-Type": "application/json",
				},
				body: JSON.stringify(track.settings),
			});

			if (!processResponse.ok) {
				throw new Error("Failed to start regeneration");
			}

			// Initial check to update localStorage
			const initialStatusResponse = await fetch(
				`/api/tracks/${track.id}/status`
			);
			if (initialStatusResponse.ok) {
				const data = await initialStatusResponse.json();
				localStorage.setItem(`processing_${track.id}`, data.status);
			}

			// The polling will now be handled by the useEffect
		} catch (error) {
			console.error("Regeneration error:", error);
			toast({
				title: "Error",
				description: "Failed to regenerate extended mix",
				variant: "destructive",
				duration: 5000,
			});
			setIsProcessing(false);
		}
	};

	return (
		// skipcq: JS-0415
		<div>
			<div className='flex flex-col md:flex-row items-center md:items-start gap-4 mb-6'>
				<div className='w-32 h-32 bg-gradient-to-br from-gray-800 to-gray-900 rounded-lg flex items-center justify-center shadow-md flex-shrink-0'>
					<span className='material-icons text-white text-5xl'>
						{type === "original" ? "music_note" : "equalizer"}
					</span>
				</div>

				<div className='flex-1'>
					<div className='text-center md:text-left mb-2'>
						<h3 className='text-xl font-bold'>{displayTitle}</h3>
						<p className='text-gray-500'>{displayDetails}</p>
					</div>

					<div className='flex flex-col gap-2 text-sm'>
						<div className='flex flex-wrap items-center gap-4'>
							<div className='flex items-center gap-2'>
								<span className='text-gray-500'>Format:</span>
								<span className='font-medium'>
									{track.format
										? `${track.format.toUpperCase()}${
												track.bitrate
													? `, ${Math.round(track.bitrate / 1000)}kbps`
													: ""
										  }`
										: "Unknown"}
								</span>
							</div>
							<div className='flex items-center gap-2'>
								<span className='text-gray-500'>Key:</span>
								<span className='font-medium'>{track.key || "Unknown"}</span>
							</div>
						</div>
						<div className='flex flex-wrap items-center gap-4'>
							<div className='flex items-center gap-2'>
								<span className='text-gray-500'>Tempo:</span>
								<span className='font-medium'>
									{track.bpm ? `${track.bpm} BPM` : "Unknown"}
								</span>
							</div>
							<div className='flex items-center gap-2'>
								<span className='text-gray-500'>Duration:</span>
								<span className='font-medium'>
									{formatDuration(displayDuration)}
								</span>
							</div>
						</div>
						{type === "extended" && (
							<div className='flex items-center gap-2'>
								<span className='text-gray-500'>Intro Length:</span>
								<span className='font-medium'>
									{track.settings?.introLength} bars
								</span>
							</div>
						)}
					</div>
				</div>
			</div>
			{type === "original" && (
				<div className='mb-4'>
					<h4 className='font-medium mb-2'>Waveform</h4>
					<div className='waveform-container bg-gray-900 rounded-lg'>
						<div className='waveform'>
							<div className='waveform-bars flex items-center h-full p-4'>
								{Array(type === "original" ? 120 : 150)
									.fill(0)
									.map((_, i) => {
										const isIntroSection =
											type === "extended" &&
											i <=
												((track.settings?.introLength || 16) / track.bpm) *
													60 *
													(150 / duration);
										const isCurrentlyPlaying =
											i / (type === "original" ? 120 : 150) <=
											currentTime / duration;

										return (
											<div
												key={`waveform-bar-${i}`} // skipcq: JS-0437
												className={
													"waveform-bar transition-colors duration-300"
												}
												style={{
													height: `${Math.floor(Math.random() * 70 + 10)}px`,
													width: "100%",
													margin: "0 1px",
													background:
														type === "extended"
															? isCurrentlyPlaying
																? isIntroSection
																	? "linear-gradient(to top, #10b981, #34d399)"
																	: "linear-gradient(to top, #7c3aed, #a78bfa)"
																: isIntroSection
																? "linear-gradient(to top, #064e3b, #065f46)"
																: "linear-gradient(to top, #4c1d95, #5b21b6)"
															: isCurrentlyPlaying
															? "linear-gradient(to top, #7c3aed, #a78bfa)"
															: "linear-gradient(to top, #4b5563, #6b7280)",
												}}></div>
										);
									})}
							</div>
						</div>
					</div>
					{/* skipcq: JS-0417, JS-0746 */}
					<div
						className='player-progress mt-2 mb-2 h-2 bg-gray-200 rounded-full overflow-hidden cursor-pointer relative'
						// skipcq: JS-0417
						onClick={handleProgressClick} // skipcq: JS-0746
						onKeyDown={handleProgressKeyDown} // skipcq: JS-0417
						tabIndex={0} // Make focusable for keyboard navigation
						role='slider' // Semantic role for screen readers
						aria-label='Audio progress'
						aria-valuemin={0}
						aria-valuemax={displayDuration}
						aria-valuenow={currentTime}>
						{type === "extended" && (
							<div
								className='h-full bg-gradient-to-r from-emerald-500 to-emerald-400 absolute'
								style={{
									width: `${
										((track.settings?.introLength || 16) / track.bpm) *
										60 *
										(100 / duration)
									}%`,
								}}
							/>
						)}
						<div
							className='h-full bg-gradient-to-r from-purple-600 to-purple-400 transition-all duration-300'
							style={{
								width: `${(currentTime / (duration || 1)) * 100}%`,
							}}
						/>
					</div>

					<div className='player-controls flex items-center'>
						<button
							className='p-2 rounded-full hover:bg-gray-100'
							// skipcq: JS-0417
							onClick={handleSkipBack}>
							<span className='material-icons'>skip_previous</span>
						</button>
						<button
							className='p-2 rounded-full hover:bg-gray-100'
							// skipcq: JS-0417
							onClick={togglePlayPause}>
							<span className='material-icons'>
								{isPlaying ? "pause" : "play_arrow"}
							</span>
						</button>
						<button
							className='p-2 rounded-full hover:bg-gray-100'
							// skipcq: JS-0417
							onClick={handleSkipForward}>
							<span className='material-icons'>skip_next</span>
						</button>
						<span className='text-sm text-gray-500 ml-2 -mt-2'>
							{formatDuration(currentTime)} / {formatDuration(duration)}
						</span>
					</div>
				</div>
			)}
			{type === "extended" && track.extendedPaths?.length > 0 && (
				<div className='mt-4'>
					<div className='flex items-center gap-4 mb-2 text-xs'>
						<div className='flex items-center gap-1'>
							<div className='w-3 h-3 rounded-sm bg-gradient-to-t from-[#10b981] to-[#34d399]'></div>
							<span>Intro</span>
						</div>
						<div className='flex items-center gap-1'>
							<div className='w-3 h-3 rounded-sm bg-gradient-to-t from-[#7c3aed] to-[#a78bfa]'></div>
							<span>Main</span>
						</div>
					</div>
					<div className='grid grid-cols-1 gap-6'>
						{(track.extendedPaths || []).map((path, idx) => (
							<VersionPlayer
								key={`${track.id}-version-${idx}`} // skipcq: JS-0437
								track={track}
								version={idx}
							/>
						))}
						{(track.extendedPaths?.length || 0) < 3 && (
							<div>
								<div className='text-sm text-gray-500 mb-2'>
									Regenerations remaining:{" "}
									{2 - ((track.extendedPaths?.length || 1) - 1)}
								</div>
								<button
									className='inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-primary hover:bg-primary-dark focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary disabled:opacity-50'
									// skipcq: JS-0417
									onClick={handleRegenerate}
									disabled={
										track.status === "processing" ||
										isProcessing ||
										(track.extendedPaths?.length || 0) >= 3
									}>
									{isProcessing ? (
										<>
											<svg
												className='animate-spin -ml-1 mr-2 h-4 w-4 text-white'
												xmlns='http://www.w3.org/2000/svg'
												fill='none'
												viewBox='0 0 24 24'>
												<circle
													className='opacity-25'
													cx='12'
													cy='12'
													r='10'
													stroke='currentColor'
													strokeWidth='4'></circle>
												<path
													className='opacity-75'
													fill='currentColor'
													d='M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z'></path>
											</svg>
											Processing...
										</>
									) : (
										<>
											<span className='material-icons text-sm mr-1'>
												autorenew
											</span>
											Regenerate Extended Mix
										</>
									)}
								</button>
							</div>
						)}
					</div>
				</div>
			)}

			<audio
				// skipcq: JS-0754
				ref={audioRef}
				src={`/api/audio/${track.id}/${type}`}
				preload='metadata'
				style={{ display: "none" }}
				aria-label={`Music track: ${displayTitle}`}
			/>
		</div>
	);
};

export default TrackView;

