			// nosemgrep: javascript.express.express-fs-filename.express-fs-filename
			if (!fs.existsSync(filePath)) {
				return res
					.status(404)
					.json({ message: "Extended audio file not found on disk" });
			}

			// Extract original filename without extension
			const originalNameWithoutExt = path.basename(
				track.originalFilename,
				path.extname(track.originalFilename)
			);

			// Create download filename with version number
			const downloadFilename = `${originalNameWithoutExt}_extended_v${
				version + 1
			}${path.extname(track.originalFilename)}`;

			res.download(filePath, downloadFilename); // nosemgrep: javascript.lang.security.audit.path-traversal.path-join-resolve-traversal
		} catch (error) {
			console.error("Download error:", error);
			return res.status(500).json({
				message: "Error downloading file",
				error: error instanceof Error ? error.message : "Unknown error",
			});
		}
	});

	// Add streaming upload routes for large file support
	app.use("/api/streaming", streamingRoutes);

	return httpServer;
}

/**
 * Security Utilities for Path Traversal Prevention
 *
 * This module provides comprehensive security utilities to prevent path traversal
 * vulnerabilities and ensure safe file operations throughout the application.
 *
 * @format
 */

import path from "path";
import fs from "fs";
import { Request, Response, NextFunction } from "express";

/**
 * Enhanced path validation with multiple security layers
 */
export class SecurePathValidator {
	private readonly allowedDirectories: Set<string>;
	private readonly maxPathLength = 4096;
	private readonly blockedPatterns = [
		/\.\./g, // Directory traversal
		/~[/\\]/g, // Home directory references
		/\0/g, // Null byte injection
		/%00/g, // URL encoded null byte
		/%2e%2e/gi, // URL encoded ..
		/%2f/gi, // URL encoded /
		/%5c/gi, // URL encoded \
		/[<>"|*?]/g, // Filesystem dangerous characters
		/^[./\\]+/g, // Leading dots, slashes
		/CON|PRN|AUX|NUL|COM[1-9]|LPT[1-9]/gi, // Windows reserved names
	];

	constructor(allowedDirectories: string[]) {
		this.allowedDirectories = new Set(
			allowedDirectories.map((dir) => path.resolve(dir)) // nosemgrep: javascript.lang.security.audit.path-traversal.path-join-resolve-traversal.path-join-resolve-traversal
		);
	}

	/**
	 * Comprehensive path validation with multiple security checks
	 */
	validatePath(
		inputPath: string,
		operationType: "read" | "write" = "read"
	): {
		isValid: boolean;
		sanitizedPath?: string;
		errors: string[];
	} {
		const errors: string[] = [];

		// Basic input validation
		if (!inputPath || typeof inputPath !== "string") {
			errors.push("Invalid input: path must be a non-empty string");
			return { isValid: false, errors };
		}

		if (inputPath.length > this.maxPathLength) {
			errors.push(`Path length exceeds maximum (${this.maxPathLength})`);
			return { isValid: false, errors };
		}

		// Check for blocked patterns
		for (const pattern of this.blockedPatterns) {
			if (pattern.test(inputPath)) {
				errors.push(`Path contains blocked pattern: ${pattern.source}`);
				return { isValid: false, errors };
			}
		}

		try {
			// Canonicalize the path to resolve all relative components
			const canonicalPath = path.resolve(inputPath); // nosemgrep: javascript.lang.security.audit.path-traversal.path-join-resolve-traversal.path-join-resolve-traversal

			// Verify the path is within allowed directories
			const isWithinAllowedDir = Array.from(this.allowedDirectories).some(
				(allowedDir) => {
					return (
						canonicalPath.startsWith(allowedDir + path.sep) ||
						canonicalPath === allowedDir
					);
				}
			);

			if (!isWithinAllowedDir) {
				errors.push("Path is outside allowed directories");
				return { isValid: false, errors };
			}

			// Additional validation for write operations
			if (operationType === "write") {
				const parentDir = path.dirname(canonicalPath);
				if (!this.isDirectoryWritable(parentDir)) {
					errors.push("Parent directory is not writable");
					return { isValid: false, errors };
				}
			}

			return {
				isValid: true,
				sanitizedPath: canonicalPath,
				errors: [],
			};
		} catch (error) {
			errors.push(
				`Path resolution failed: ${
					error instanceof Error ? error.message : "Unknown error"
				}`
			);
			return { isValid: false, errors };
		}
	}

	/**
	 * Secure filename sanitization
	 */
	// skipcq: JS-0105
	sanitizeFilename(filename: string): string {
		if (!filename || typeof filename !== "string") {
			throw new Error("Invalid filename input");
		}

		return filename
			.replace(/[<>:"/\\|?*\0]/g, "") // Remove dangerous characters
			.replace(/\.\./g, "") // Remove path traversal attempts
			.replace(/^\.+/, "") // Remove leading dots
			.replace(/\s+/g, "_") // Replace spaces with underscores
			.slice(0, 255) // Limit filename length
			.trim();
	}

	/**
	 * Validate file extension against allowlist
	 */
	// skipcq: JS-0105
	validateFileExtension(
		filename: string,
		allowedExtensions: string[]
	): boolean {
		const ext = path.extname(filename).toLowerCase();
		return allowedExtensions.includes(ext);
	}

	/**
	 * Check if directory is writable
	 */
	// skipcq: JS-0105
	private isDirectoryWritable(dirPath: string): boolean {
		try {
			fs.accessSync(dirPath, fs.constants.W_OK);
			return true;
		} catch {
			return false;
		}
	}
}

/**
 * Input sanitization utilities
 */
// skipcq: JS-0327
export class InputSanitizer {
	/**
	 * Sanitize and validate integer parameters
	 */
	static sanitizeIntParam(
		value: unknown,
		min?: number,
		max?: number
	): number | null {
		const parsed = parseInt(String(value), 10);

		if (isNaN(parsed)) {
			return null;
		}

		if (min !== undefined && parsed < min) {
			return null;
		}

		if (max !== undefined && parsed > max) {
			return null;
		}

		return parsed;
	}

	/**
	 * Sanitize string parameters
	 */
	static sanitizeStringParam(
		value: unknown,
		allowedValues?: string[],
		maxLength = 1000
	): string | null {
		if (typeof value !== "string") {
			return null;
		}

		if (value.length > maxLength) {
			return null;
		}

		// Remove potential XSS and injection attempts
		const sanitized = value.replace(/[<>"\0]/g, "").trim();

		if (allowedValues && !allowedValues.includes(sanitized)) {
			return null;
		}

		return sanitized || null;
	}

	/**
	 * Validate job ID format (alphanumeric with specific format)
	 */
	static validateJobId(jobId: unknown): string | null {
		if (typeof jobId !== "string") {
			return null;
		}

		// Job IDs should be alphanumeric, possibly with hyphens/underscores
		const jobIdPattern = /^[a-zA-Z0-9\-_]{1,64}$/;

		if (!jobIdPattern.test(jobId)) {
			return null;
		}

		return jobId;
	}
}

/**
 * Security middleware factory
 */
export function createSecurityMiddleware(validator: SecurePathValidator) {
	return {
		/**
		 * Validate file paths in requests
		 */
		// skipcq: JS-0045
		validateFilePaths: (req: Request, res: Response, next: NextFunction) => {
			// Check for file paths in common request locations
			const pathsToCheck = [
				req.body.filePath,
				req.body.path,
				req.query.filePath,
				req.query.path,
			].filter(Boolean);

			for (const pathToCheck of pathsToCheck) {
				const validation = validator.validatePath(pathToCheck);
				if (!validation.isValid) {
					return res.status(400).json({
						message: "Invalid file path",
						errors: validation.errors,
					});
				}
			}

			next();
		},

		/**
		 * Rate limiting for file operations
		 */
		rateLimitFileOps: (() => {
			const requestCounts = new Map<
				string,
				{ count: number; resetTime: number }
			>();
			const maxRequests = 100;
			const windowMs = 15 * 60 * 1000; // 15 minutes

			// skipcq: JS-0045
			return (req: Request, res: Response, next: NextFunction) => {
				const clientIp = req.ip || req.connection.remoteAddress;
				const now = Date.now();
				if (!requestCounts.has(clientIp)) {
					requestCounts.set(clientIp, { count: 1, resetTime: now + windowMs });
					return next();
				}

				const clientData = requestCounts.get(clientIp)!; // skipcq: JS-0339

				if (now > clientData.resetTime) {
					clientData.count = 1;
					clientData.resetTime = now + windowMs;
					return next();
				}

				if (clientData.count >= maxRequests) {
					return res.status(429).json({
						message: "Too many file operation requests",
					});
				}

				clientData.count++;
				next();
			};
		})(),
	};
}

/**
 * Streaming Upload API Routes
 *
 * Provides endpoints for:
 * - Initiating streaming uploads with progress tracking
 * - Monitoring upload progress in real-time
 * - Processing large audio files efficiently
 * - Managing upload lifecycle and cleanup
 * - Integration with existing Music DJ Feature infrastructure
 *
 * @format
 */

import { Router, Request, Response } from "express";
import path from "path";
import crypto from "crypto";
import { promises as fs } from "fs";
import {
	createStreamingUploader,
	trackUploadProgress,
	getUploadProgress,
	updateUploadProgress,
	cleanupUploadProgress,
	handleStreamingErrors,
	AudioFileStreamProcessor,
	uploadProgress,
} from "./streaming-upload.js";
import { storage } from "./storage.js";

// Interface for active upload summary
interface ActiveUploadSummary {
	uploadId: string;
	filename: string;
	percentage: number;
	status: "uploading" | "processing";
	bytesReceived: number;
	totalBytes: number;
	speed: number;
	estimatedTimeRemaining: number;
}

const router = Router();
const streamProcessor = new AudioFileStreamProcessor();

// Get normalized directories (same as routes.ts)
const normalizedUploadsDir = path.resolve(process.cwd(), "uploads");
const normalizedResultDir = path.resolve(process.cwd(), "results");

/**
 * Initialize streaming upload session
 * POST /api/streaming/upload/init
 *
 * Prepares the server for a streaming upload and returns upload ID
 */
// skipcq: JS-0045
router.post("/upload/init", async (req: Request, res: Response) => {
	try {
		const { filename, fileSize } = req.body;

		// Validate input
		if (!filename || !fileSize) {
			return res.status(400).json({
				error: "Missing required fields",
				message: "filename and fileSize are required",
			});
		}

		// Check file size limit (500MB)
		const maxSize = 500 * 1024 * 1024;
		if (fileSize > maxSize) {
			return res.status(413).json({
				error: "File too large",
				message: `Maximum file size is ${Math.round(
					maxSize / (1024 * 1024)
				)}MB`,
			});
		}

		// Validate file format
		const allowedExtensions = [
			".mp3",
			".wav",
			".flac",
			".aiff",
			".m4a",
			".ogg",
		];
		const ext = path.extname(filename).toLowerCase();
		if (!allowedExtensions.includes(ext)) {
			return res.status(400).json({
				error: "Unsupported format",
				message: `Allowed formats: ${allowedExtensions.join(", ")}`,
			});
		}

		// Generate upload session
		const uploadId = crypto.randomBytes(16).toString("hex");

		// Initialize progress tracking
		updateUploadProgress(uploadId, {
			uploadId,
			filename,
			bytesReceived: 0,
			totalBytes: fileSize,
			percentage: 0,
			status: "uploading",
			startTime: Date.now(),
			speed: 0,
			estimatedTimeRemaining: 0,
		});

		res.json({
			uploadId,
			chunkSize: 1024 * 1024, // 1MB chunks
			maxFileSize: maxSize,
			message: "Upload session initialized",
		});
	} catch (error) {
		console.error("Upload init error:", error);
		res.status(500).json({
			error: "Server error",
			message: "Failed to initialize upload session",
		});
	}
});

/**
 * Enhanced streaming upload endpoint with database integration
 * POST /api/streaming/upload/stream
 *
 * Handles the actual file upload with real-time progress and database integration
 */
router.post(
	"/upload/stream",
	trackUploadProgress(),
	createStreamingUploader({
		maxFileSize: 500 * 1024 * 1024, // 500MB
		uploadDirectory: normalizedUploadsDir,
		chunkSize: 1024 * 1024, // 1MB chunks
	}),
	// skipcq: JS-0045
	async (req: Request, res: Response) => {
		try {
			const uploadId = req.uploadId;
			const file = req.file;

			if (!file) {
				return res.status(400).json({
					error: "No file uploaded",
					message: "Please select an audio file to upload",
					uploadId,
				});
			}

			// Update progress to processing state
			// skipcq: JS-0339
			updateUploadProgress(uploadId!, {
				status: "processing",
				percentage: 100,
			});

			// Get demo user (same as existing routes.ts)
			let demoUser = await storage.getUserByUsername("demo");
			if (!demoUser) {
				demoUser = await storage.createUser({
					username: "demo",
					password: "password",
				});
			}

			// Create database entry (integrate with existing storage)
			const track = await storage.createAudioTrack({
				originalFilename: file.originalname,
				originalPath: file.path,
				userId: demoUser.id,
			});

			// Process the uploaded file for metadata
			const result = await streamProcessor.processAudioFile(
				file.path,
				uploadId! // skipcq: JS-0339
			);

			// Update track with metadata (same as existing flow)
			if (result.metadata) {
				await storage.updateAudioTrack(track.id, {
					format: result.metadata.format,
					bitrate: result.metadata.bitrate || null,
					duration: result.metadata.duration || null,
					bpm: result.metadata.bpm || null,
					key: result.metadata.key || null,
				});
			}

			// Final progress update
			// skipcq: JS-0339
			updateUploadProgress(uploadId!, {
				status: "completed",
				percentage: 100,
			});

			// Return response in same format as existing upload endpoint
			res.status(201).json({
				...track,
				id: track.id,
				originalFilename: track.originalFilename,
				originalPath: track.originalPath,
				userId: track.userId,
				status: track.status,
				format: result.metadata?.format,
				bitrate: result.metadata?.bitrate,
				duration: result.metadata?.duration,
				bpm: result.metadata?.bpm,
				key: result.metadata?.key,
				uploadId,
				streaming: true, // Flag to indicate this was a streaming upload
				message: "Large file uploaded and processed successfully",
			});
		} catch (error) {
			console.error("Streaming upload error:", error);
			const uploadId = req.uploadId;

			if (uploadId) {
				updateUploadProgress(uploadId, {
					status: "error",
					error: error instanceof Error ? error.message : "Upload failed",
				});
			}

			res.status(500).json({
				error: "Upload failed",
				message: error instanceof Error ? error.message : "Unknown error",
				uploadId,
			});
		}
	}
);

/**
 * Get upload progress
 * GET /api/streaming/upload/progress/:uploadId
 *
 * Returns real-time progress information for an upload
 */
// skipcq: JS-0045
router.get("/upload/progress/:uploadId", (req: Request, res: Response) => {
	try {
		const { uploadId } = req.params;
		const progress = getUploadProgress(uploadId);

		if (!progress) {
			return res.status(404).json({
				error: "Upload not found",
				message: "No upload found with the specified ID",
			});
		}

		res.json({
			...progress,
			timestamp: new Date().toISOString(),
		});
	} catch (error) {
		console.error("Progress check error:", error);
		res.status(500).json({
			error: "Server error",
			message: "Failed to retrieve upload progress",
		});
	}
});

/**
 * Cancel/cleanup upload
 * DELETE /api/streaming/upload/:uploadId
 *
 * Cancels an ongoing upload and cleans up resources
 */
// skipcq: JS-0045
router.delete("/upload/:uploadId", async (req: Request, res: Response) => {
	try {
		const { uploadId } = req.params;
		const progress = getUploadProgress(uploadId);

		if (!progress) {
			return res.status(404).json({
				error: "Upload not found",
				message: "No upload found with the specified ID",
			});
		}

		// Clean up any temporary files
		const tempPattern = path.join(normalizedUploadsDir, `${uploadId}_*`); // nosemgrep: javascript.express.security.audit.express-path-join-resolve-traversal.express-path-join-resolve-traversal, javascript.lang.security.audit.path-traversal.path-join-resolve-traversal.path-join-resolve-traversal

		try {
			await streamProcessor.cleanup(tempPattern);
		} catch (error) {
			// Ignore cleanup errors as files might not exist
			console.warn("Cleanup warning:", error);
		}

		// Remove from progress tracking
		cleanupUploadProgress(uploadId);

		res.json({
			success: true,
			message: "Upload cancelled and cleaned up",
			uploadId,
		});
	} catch (error) {
		console.error("Upload cleanup error:", error);
		res.status(500).json({
			error: "Cleanup failed",
			message: "Failed to clean up upload resources",
		});
	}
});

/**
 * Get all active uploads (admin endpoint)
 * GET /api/streaming/upload/active
 *
 * Returns list of all currently active uploads
 */
router.get("/upload/active", (req: Request, res: Response) => {
	try {
		// In production, add authentication/authorization here
		const activeUploads: ActiveUploadSummary[] = [];

		// Get all active uploads (in production, this should be paginated)
		uploadProgress.forEach((progress, _uploadId) => {
			if (progress.status === "uploading" || progress.status === "processing") {
				activeUploads.push({
					uploadId: progress.uploadId,
					filename: progress.filename,
					percentage: progress.percentage,
					status: progress.status,
					bytesReceived: progress.bytesReceived,
					totalBytes: progress.totalBytes,
					speed: progress.speed,
					estimatedTimeRemaining: progress.estimatedTimeRemaining,
				});
			}
		});

		res.json({
			activeUploads,
			count: activeUploads.length,
			timestamp: new Date().toISOString(),
		});
	} catch (error) {
		console.error("Active uploads error:", error);
		res.status(500).json({
			error: "Server error",
			message: "Failed to retrieve active uploads",
		});
	}
});

/**
 * Health check for streaming service
 * GET /api/streaming/health
 */
router.get("/health", async (req: Request, res: Response) => {
	try {
		const uploadsDir = normalizedUploadsDir;
		const tempDir = path.join(uploadsDir, "temp");
		// Audio directory available if needed: path.join(uploadsDir, "audio")

		// Check directory accessibility
		await fs.access(uploadsDir);

		res.json({
			status: "healthy",
			service: "streaming-upload",
			version: "1.0.0",
			integration: "music-dj-feature",
			directories: {
				uploads: uploadsDir,
				temp: tempDir,
				results: normalizedResultDir,
			},
			limits: {
				maxFileSize: "500MB",
				chunkSize: "1MB",
				allowedFormats: [".mp3", ".wav", ".flac", ".aiff", ".m4a", ".ogg"],
			},
			features: {
				databaseIntegration: true,
				metadataExtraction: true,
				progressTracking: true,
				errorRecovery: true,
			},
			timestamp: new Date().toISOString(),
		});
	} catch (error) {
		res.status(503).json({
			status: "unhealthy",
			error: error instanceof Error ? error.message : "Unknown error",
			timestamp: new Date().toISOString(),
		});
	}
});

// Apply error handling middleware
router.use(handleStreamingErrors());

export default router;

